import { Link, useLocation } from "react-router-dom";
import React, { useState, useEffect } from "react";

const Nav = () => {
  const [scrolled, $setScrolled] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      $setScrolled(window.scrollY > 100);
    });
  }, []);
  const lienActif = useLocation();
  return (
    <div className="navbar">
      <nav className="headernav">
        <Link
          to="/"
          className={`link ${lienActif.pathname === "/" ? "actif" : ""}`}
          style={{
            color: scrolled ? "#FFFFFF" : "#000000",
          }}
        >
          Accueil
        </Link>
        <Link
          to="/Massages"
          className={`link ${
            lienActif.pathname === "/Massages" ? "actif" : ""
          }`}
          style={{
            color: scrolled ? "#FFFFFF" : "#000000",
          }}
        >
          Massage Ã  domicile
        </Link>
        <Link
          to="/Tarifs"
          className={`link ${lienActif.pathname === "/Tarifs" ? "actif" : ""}`}
          style={{
            color: scrolled ? "#FFFFFF" : "#000000",
          }}
        >
          Tarifs
        </Link>
        <div className="number"             style={{
              border: scrolled ? "2px solid #FFFFFF" : "2px s#000000",
            }}>
          <img
            src="https://i.goopics.net/hamu6r.png"
            className="telephone"
            alt="telephone"
            style={{
              color: scrolled ? "#FFFFFF" : "#000000",
            }}
          />

          <p
            className="numero"
            style={{
              color: scrolled ? "#FFFFFF" : "#000000",
            }}
          >
            07 56 84 55 27
          </p>
        </div>
      </nav>
    </div>
  );
};

export default Nav;
