import Logo from "../../assets/Headerlogo.png";
import { Link, useLocation } from "react-router-dom";
import React, { useState, useEffect } from "react";

const Header = () => {
  const [scrolled, $setScrolled] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      $setScrolled(window.scrollY > 100);
    });
  }, []);
  const lienActif = useLocation();
  return (
    <div className="header">
      <img src={Logo} className="Logo" alt="logo_header" />

      <nav className="headernav">
        <Link
          to="/Acc"
          className={`link ${lienActif.pathname === "/" ? "actif" : ""}`} href="#root"
        >
          Accueil
        </Link>
        <Link
          to="/Massages"
          className={`link ${
            lienActif.pathname === "/Massages" ? "actif" : ""
          }`}
        >
          Massage Ã  domicile
        </Link>
        <Link
          to="/Tarifs"
          className={`link ${lienActif.pathname === "/Tarifs" ? "actif" : ""}`}
        >
          Tarifs
        </Link>
        <div className="number">
          <img
            src="https://i.goopics.net/w8ytt2.png"
            className="telephone"
            alt="telephone"
          />

          <p className="numero">07 56 84 55 27</p>
        </div>
      </nav>
    </div>
  );
};

export default Header;
