import hash from 'object-hash';
import { useCallback, useEffect, useState } from 'react';
import { Consent, ConsentOptions } from './Context';
import { addServices } from './core/add-services';
import { getFromLocalStorage } from './core/local-storage/get';
import { isValidInLocalStorage } from './core/local-storage/valid';
import { updateServices } from './core/update-services';

type ConsentState = { consent: Consent[]; isBannerVisible: boolean; isDetailsVisible: boolean; hash: string };

export function useConsentState(options: ConsentOptions) {
    const [state, setState] = useState<ConsentState>({
        consent: [],
        isBannerVisible: false,
        isDetailsVisible: false,
        hash: options.customHash ?? hash(options),
    });

    useEffect(() => {
        if (!isValidInLocalStorage(state.hash)) {
            const consent = options.services.filter((service) => service?.mandatory).map((service) => service.id);

            setState((state) => ({ ...state, consent, isBannerVisible: true, isDetailsVisible: false }));
            return;
        }

        const { consent, isBannerVisible, isDetailsVisible } = getFromLocalStorage(state.hash);

        setState((state) => ({ ...state, consent, isBannerVisible, isDetailsVisible }));

        const approvedServices = options.services.filter((service) => consent.includes(service.id));
        addServices(approvedServices);
    }, [options.services, state.hash]);

    const setConsent = useCallback(
        (consent: Consent[]) => {
            setState((state) => ({ ...state, consent, isBannerVisible: false }));
            updateServices(options, consent, state.hash);
        },
        [options, state.hash]
    );

    const hasConsent = useCallback((id: Consent) => state.consent.includes(id), [state.consent]);

    const toggleBanner = useCallback(() => {
        setState((state) => ({ ...state, isBannerVisible: !state.isBannerVisible }));
    }, []);

    const toggleDetails = useCallback(() => {
        setState((state) => ({ ...state, isDetailsVisible: !state.isDetailsVisible }));
    }, []);

    return {
        consent: state.consent,
        hasConsent,
        isBannerVisible: state.isBannerVisible,
        isDetailsVisible: state.isDetailsVisible,
        toggleBanner,
        toggleDetails,
        setConsent,
    };
}
export const idPrefix = 'react-hook-consent';
export const localStorageKey = 'react-hook-consent';
export function addCodeScript(code: string, elementId: string) {
    const element = document.createElement('script');
    element.id = elementId;
    element.innerHTML = code;
    document.body.appendChild(element);
}
export function addExternalScript(src: string, elementId: string) {
    const element = document.createElement('script');
    element.id = elementId;
    element.src = src;
    element.async = true;
    document.body.appendChild(element);
}
import { ScriptCode, ScriptExternal } from '../../Context';

export function isScriptCode(x: ScriptExternal | ScriptCode): x is ScriptCode {
    return !!x.id && !!(x as ScriptCode).code;
}
import { ScriptCode, ScriptExternal } from '../../Context';

export function isScriptExternal(x: ScriptExternal | ScriptCode): x is ScriptExternal {
    return !!x.id && !!(x as ScriptExternal).src;
}
import { idPrefix } from '../../config';
import { ConsentOptions } from '../../Context';
import { addCodeScript } from './add/code-script';
import { addExternalScript } from './add/external-script';
import { isScriptCode } from './is-script-code';
import { isScriptExternal } from './is-script-external';

export function addScripts(serviceId: string, scripts: ConsentOptions['services'][number]['scripts']) {
    if (!scripts) {
        return;
    }

    for (const script of scripts) {
        const { id } = script;
        const elementId = `${idPrefix}-${serviceId}-${id}`;

        if (document.getElementById(elementId)) {
            continue;
        }

        if (isScriptExternal(script)) {
            addExternalScript(script.src, elementId);
        }

        if (isScriptCode(script)) {
            addCodeScript(script.code, elementId);
        }
    }
}
import { ConsentOptionsService } from '../Context';
import { addScripts } from './scripts/add';

export function addServices(services: ConsentOptionsService[]) {
    services.forEach(({ id, scripts }) => {
        addScripts(id, scripts);
    });
}
import { localStorageKey } from '../../config';
import { Consent } from '../../Context';

export type StoredState = { consent?: Consent[]; hash?: string };

export function getFromLocalStorage(hash: string) {
    const item = localStorage.getItem(`${localStorageKey}`);

    if (!item) {
        return { consent: [], isBannerVisible: true, isDetailsVisible: false };
    }

    const { consent, hash: storedHash } = JSON.parse(item) as StoredState;

    let isBannerVisible = false;
    if (storedHash !== hash) {
        isBannerVisible = true;
    }

    return { consent: consent && consent.length > 0 ? consent : [], isBannerVisible, isDetailsVisible: false };
}
import { localStorageKey } from '../../config';
import { StoredState } from './get';

export function isValidInLocalStorage(hash: string) {
    const item = localStorage.getItem(`${localStorageKey}`);

    if (!item) {
        return false;
    }

    const { hash: storedHash } = JSON.parse(item) as StoredState;

    if (storedHash !== hash) {
        return false;
    }

    return true;
}
import { Consent } from '../../Context';
import { localStorageKey } from '../../config';

export function saveToLocalStorage(consent: Consent[], hash: string) {
    localStorage.setItem(`${localStorageKey}`, JSON.stringify({ consent, hash, updated: new Date() }));
}
import { Cookie } from '../../Context';

export function removeCookies(cookies?: Cookie[]) {
    if (cookies) {
        for (const cookie of cookies) {
            document.cookie = cookie.pattern + '=;expires=Thu, 01 Jan 1970 00:00:00 GMT';
        }
    }
}
import { ConsentOptionsService } from '../../Context';

export function removeLocalStorage(localStorageItems?: ConsentOptionsService['localStorage']) {
    if (localStorageItems) {
        for (const localStorageItem of localStorageItems) {
            localStorage.removeItem(localStorageItem);
        }
    }
}
import { idPrefix } from '../../config';
import { ConsentOptions } from '../../Context';

export function removeScripts(serviceId: string, scripts: ConsentOptions['services'][number]['scripts']) {
    if (!scripts) {
        return;
    }

    for (const { id } of scripts) {
        const element = document.getElementById(`${idPrefix}-${serviceId}-${id}`);

        if (!element) {
            continue;
        }

        element.remove();
    }
}
import { ConsentOptionsService } from '../../Context';

export function removeSessionStorage(sessionStorageItems?: ConsentOptionsService['sessionStorage']) {
    if (sessionStorageItems) {
        for (const sessionStorageItem of sessionStorageItems) {
            sessionStorage.removeItem(sessionStorageItem);
        }
    }
}
import { ConsentOptionsService } from '../Context';
import { removeCookies } from './cookies/remove';
import { removeLocalStorage } from './local-storage/remove';
import { removeScripts } from './scripts/remove';
import { removeSessionStorage } from './session-storage/remove';

export function removeServices(services: ConsentOptionsService[]) {
    services.forEach(({ id, scripts, cookies, localStorage, sessionStorage }) => {
        removeScripts(id, scripts);
        removeCookies(cookies);
        removeLocalStorage(localStorage);
        removeSessionStorage(sessionStorage);
    });
}
import { Consent, ConsentOptions } from '../Context';
import { addServices } from './add-services';
import { saveToLocalStorage } from './local-storage/save';
import { removeServices } from './remove-services';

export function updateServices(options: ConsentOptions, consent: Consent[], hash: string) {
    const removedServices = options.services.filter((service) => !consent.includes(service.id));
    const addedServices = options.services.filter((service) => consent.includes(service.id));

    removeServices(removedServices);
    addServices(addedServices);

    saveToLocalStorage(consent, hash);
}
import { ConsentContext, ConsentOptions } from './Context';
import { useConsentState } from './useConsentState';

type ConsentProviderProps = {
    options: ConsentOptions;
    children: React.ReactNode;
};

export function ConsentProvider({ options, children }: ConsentProviderProps) {
    const { consent, hasConsent, isBannerVisible, toggleBanner, isDetailsVisible, toggleDetails, setConsent } =
        useConsentState(options);

    return (
        <ConsentContext.Provider
            value={{
                consent,
                hasConsent,
                isBannerVisible,
                toggleBanner,
                isDetailsVisible,
                toggleDetails,
                setConsent,
                options,
            }}
        >
            {children}
        </ConsentContext.Provider>
    );
}
