import { Link, useLocation } from "react-router-dom";
import React, { useState, useEffect } from "react";

const Nav = () => {
  const [scrolled, $setScrolled] = useState(false);

  useEffect(() => {
    window.addEventListener("scroll", () => {
      $setScrolled(window.scrollY > 100);
    });
  }, []);
  const lienActif = useLocation();
  return (
    <div className="navbar">
      <nav className="headernav">
        <Link
          to="/"
          className={`link ${lienActif.pathname === "/" ? "actif" : ""}`} style={{
            background: scrolled ? "#794C3C" : "transparent", 
          }}
        >
          Accueil
        </Link>
        <Link
          to="/Massages"
          className={`link ${
            lienActif.pathname === "/Massages" ? "actif" : ""
          }`}
        >
          Massage Ã  domicile
        </Link>
        <Link
          to="/Tarifs"
          className={`link ${lienActif.pathname === "/Tarifs" ? "actif" : ""}`}
        >
          Tarifs
        </Link>
        <div className="menu">
          <img
            src="https://i.goopics.net/4ujmmk.png"
            className="telephone"
            alt="telephone"
          />

            <p className="numero" >07 56 84 55 27</p>

        </div>
      </nav>
    </div>
  );
};

export default Nav;
